import React, { useState } from 'react';
import axios from 'axios';
import FormData from 'form-data';
import './App.css';

const OPENAI_API_KEY = process.env.REACT_APP_API_KEY;
const model = "whisper-1";

function App() {
  const [transcription, setTranscription] = useState("");

  const transcribe = (file) => {
    const formData = new FormData();
    formData.append("model", model);
    formData.append("file", file);

    fetch("https://api.openai.com/v1/audio/transcriptions", formData,{
        headers: {
            Authorization: `Bearer ${OPENAI_API_KEY}`,
            "Content-Type": `multipart/form-data"; boundary =${formData._boundary}`,
        },
    })
    .then((response) => {
        console.log(response.data);
        setTranscription(response.data.text);

    })
    .catch((error) => {
        console.error(error);
    });
    }
  async function handleFileChange(event) {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      transcribe(selectedFile);
    }
  }
  
  return (    
    <div className="App">
    <div className="center-container">
    <h1 className="sync-title">Sync</h1>
    <div className="search-bar"> 
    <input type="file" accept=".mp3,.mp4" onChange={handleFileChange} /> 
    </div>
    <div>{transcription}</div>
    </div>
    </div>
    );
}

export default App;
