import React, { useState } from 'react';
import axios from 'axios';
import FormData from 'form-data';
import './App.css';
import syncLogo from './Sync.png'; 

const OPENAI_API_KEY = process.env.REACT_APP_API_KEY;
const model = "whisper-1";

function App() {
  const [transcription, setTranscription] = useState("");

  const transcribe = (file) => {
    const formData = new FormData();
    formData.append("model", model);
    formData.append("file", file);
    axios
      .post("https://api.openai.com/v1/audio/transcriptions", formData, {
        headers: {
          Authorization: `Bearer ${OPENAI_API_KEY}`,
          "Content-Type": `multipart/form-data; boundary=${formData._boundary}`,
        },
      })
      .then((response) => {
        console.log(response.data);
        setTranscription(response.data.text);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  async function handleFileChange(event) {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      transcribe(selectedFile);
    }
  }

  function refreshPage() {
    window.location.reload(false);
  }

  return (
    <div className="App">
      <header className="App-header">
        <span>Sync</span> {/* Left-aligned text */}
        <span>v1</span> {/* Right-aligned text */}
      </header>
      <div className="center-container">
        {/* Clickable Sync logo that refreshes the page */}
        <img src={syncLogo} alt="Sync logo" className="sync-logo" onClick={refreshPage} />
        <div className="search-bar">
          <input type="file" accept=".mp3,.mp4" onChange={handleFileChange} />
        </div>
        <div>{transcription}</div>
      </div>
    </div>
  );
}

export default App;
